    private void btnPossoVotarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnPossoVotarActionPerformed
        // TODO add your handling code here:
        int anoNascimento = Integer.parseInt(txtAnoNascimento.getText());
        int idade = 2020 - anoNascimento;
        if(idade < 16){
            lblResultado.setText("Voto Proibido.");
        }else if((idade >= 16 && idade < 18) || (idade > 70)){
            lblResultado.setText("Voto Opcional.");
        }else{
            lblResultado.setText("Voto Obrigat√≥rio.");
        }
    }//GEN-LAST:event_btnPossoVotarActionPerformed

